import React, { Component } from 'react'
import { Menu, Segment, Container } from 'semantic-ui-react'
import MemoryGame from './MemoryGame';

export default class Portfolio extends Component {
  state = { activeItem: 'Word Guess' }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  render() {
    const { activeItem } = this.state

    const { showMemoryGame } = this.state

    console.log(this.state)

    return (
      <Container className="portfolio">
        <Menu attached='top' tabular color="grey" inverted>
          <Menu.Item
            name='Word Guess'
            active={activeItem === 'Word Guess'}
            onClick={this.handleItemClick}
          />
          <Menu.Item
            name='Trivia Game'
            active={activeItem === 'Trivia Game'}
            onClick={this.handleItemClick}
          />
          <Menu.Item
            name='Crystal Game'
            active={activeItem === 'Crystal Game'}
            onClick={this.handleItemClick}
          />
          <Menu.Item
            name='Friend Finder'
            active={activeItem === 'Friend Finder'}
            onClick={this.handleItemClick}
          />
          <Menu.Item
            name='Memory Game'
            active={activeItem === 'Memory Game'}
            onClick={this.handleItemClick}
          />
          <Menu.Item
            name='Nerdstillery'
            active={activeItem === 'Nerdstillery'}
            onClick={this.handleItemClick}
          />
        </Menu>

        <Segment attached='bottom'>
          { this.activeItem === "Memory Game" ? <MemoryGame /> : "" }
        </Segment>
      </Container>
    )
  }
}
