import React, { Component } from 'react'
import { Grid, Menu, Segment, Container } from 'semantic-ui-react'

export default class Portfolio extends Component {
  state = { activeItem: 'bio' }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  render() {
    const { activeItem } = this.state

    return (
      <Container className="portfolio">
      <Grid>
        <Grid.Column width={4}>
          <Menu fluid vertical tabular>
            <Menu.Item
              name='Word Guess'
              active={activeItem === 'Word Guess'}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              name='Trivia Game'
              active={activeItem === 'Trivia Game'}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              name='Crystal Game'
              active={activeItem === 'Crystal Game'}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              name='Friend Finder'
              active={activeItem === 'Friend Finder'}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              name='Memory Game'
              active={activeItem === 'Memory Game'}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              name='Nerdstillery'
              active={activeItem === 'Nerdstillery'}
              onClick={this.handleItemClick}
            />
          </Menu>
        </Grid.Column>

        <Grid.Column stretched width={12}>
          <Segment>

          </Segment>
        </Grid.Column>
      </Grid>
      </Container>
    )
  }
}
