import React from 'react';
import emailjs from 'emailjs-com';
import { Form, Button, Container, Icon, Grid } from 'semantic-ui-react'

export default function Contact() {

  function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('service_2fq9q6k', 'template_wd63p6p', e.target, 'user_Y82jhxjxWDXbp8YZSuP8S')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
      e.target.reset();
  }

  return (
      <Container>
          <Grid columns={1} centered>
                  <Grid.Column>
        <Form onSubmit={sendEmail} size="large">
            <Form.Field width="5">
                <label>Name</label>
                <input placeholder='Name' type="text" name="name" />
            </Form.Field>
        <Form.Field width="5">
                <label>Email</label>
                <input placeholder='Email' type="email" name="email" />
        </Form.Field>
        <Form.Field width="10">
                <label>Message</label>
                <input placeholder='Message' type="text" name="message" />
        </Form.Field>
            <Button type='submit' color="primary"><Icon name="mail" />Submit</Button>
        </Form>
        </Grid.Column>
        </Grid>
        </Container>
  );
}
