import React from 'react';
import emailjs from 'emailjs-com';
import { Form, Button, Container, Icon } from 'semantic-ui-react'

class Contact extends React.Component {
    constructor(props) {
        super(props)

    this.state = {
        name: '',
        email: '',
        message: ''
    }
}

    namechange = (event, data) => {
        this.setState({
          name: data.value
        })
      }

      emailchange = event => {
        this.setState({
          email: event.target.value
        })
      }

      messagechange = event => {
        this.setState({
          message: event.target.value
        })
      }

  sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('service_2fq9q6k', 'template_wd63p6p', e.target, 'user_Y82jhxjxWDXbp8YZSuP8S')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
      e.target.reset();
  }

  render() {

  return (
      <Container>
        <Form onSubmit={sendEmail} size="large">
            <Form.Field required>
                <label>Name</label>
                <input placeholder='Name' type="text" name="name" value={this.state.name} onChange={(e) => this.namechange(e)}/>
            </Form.Field>
        <Form.Field required>
                <label>Email</label>
                <input placeholder='Email' type="email" name="email" value={this.state.email} onChange={(e) => this.emailchange(e)}/>
        </Form.Field>
        <Form.Field required>
                <label>Message</label>
                <input placeholder='Message' type="text" name="message" value={this.state.message} onChange={(e) => this.messagechange(e)}/>
        </Form.Field>
            <Button type='submit' color="primary"><Icon name="mail" />Submit</Button>
        </Form>
        </Container>
  );
}
}

export default Contact;
