import React from 'react';
import emailjs from 'emailjs-com';
import { Form, Button, Container, Icon } from 'semantic-ui-react'

export default function Contact() {

    this.state = {
        name: '',
        email: '',
        message: ''
    }

    namechange = (event, data) => {
        this.setState({
          name: data.value
        })
      }

      emailchange = event => {
        this.setState({
          email: event.target.value
        })
      }

      messagechange = event => {
        this.setState({
          message: event.target.value
        })
      }

  function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('service_2fq9q6k', 'template_wd63p6p', e.target, 'user_Y82jhxjxWDXbp8YZSuP8S')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
      e.target.reset();
  }

  return (
      <Container>
        <Form onSubmit={sendEmail} size="large">
            <Form.Field required value={this.state.name} onChange={(e) => this.namechange(e)}>
                <label>Name</label>
                <input placeholder='Name' type="text" name="name" />
            </Form.Field>
        <Form.Field required value={this.state.email}>
                <label>Email</label>
                <input placeholder='Email' type="email" name="email" />
        </Form.Field>
        <Form.Field required value={this.state.message}>
                <label>Message</label>
                <input placeholder='Message' type="text" name="message" />
        </Form.Field>
            <Button type='submit' color="primary"><Icon name="mail" />Submit</Button>
        </Form>
        </Container>
  );
}
