import 'semantic-ui-css/semantic.min.css';
import NavBar from "./components/NavBar";
import JamesImage from './components/JamesImage';
import JobTitle from './components/JobTitle';
import About from './components/About';

import './style.css';
import { Component } from 'react';
import Portfolio from './components/Portfolio';

export default class App extends Component {
  constructor(props) {
    super(props)

    this.toggleHome = this.toggleHome.bind(this);
    this.toggleAbout = this.toggleAbout.bind(this);
    this.togglePortfolio = this.togglePortfolio.bind(this);

    this.state = {
      showHome: false,
      showAbout: true,
      showPortfolio: false
    }
  }

  toggleHome (event) {
    event.preventDefault()
    this.setState({
      showHome: true,
      showAbout: false,
      showPortfolio: false
    })
  }

  toggleAbout (event) {
    event.preventDefault()
    this.setState({
      showHome: false,
      showAbout: true,
      showPortfolio: false
    })
  }

  togglePortfolio (event) {
    event.preventDefault()
    this.setState({
      showHome: false,
      showAbout: false,
      showPortfolio: true
    })
  }

  render() {
    const { showHome } = this.state;
    const { showAbout } = this.state;
    const { showPortfolio } = this.state;
  return (
    <div>
      <NavBar 
        home = { this.onClick=this.toggleHome }
        about = { this.onClick=this.toggleAbout } 
        portfolio = { this.onClick=this.togglePortfolio } 
      />
      <JamesImage />
      <JobTitle />

      { showAbout === true ? <About /> : "" }
      { showPortfolio === true ? <Portfolio /> : ""}
   </div>
  );
}
}
